package test.servicea.domain;

import jakarta.persistence.Entity;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;

/**
 * Entity representing a Picture.
 * This class serves as a data model for the "Picture" entity, typically
 * representing information about a picture, such as its name, stock, price, and description.
 */
@Entity
public class Picture {

  /**
   * The unique identifier for the "Picture" entity.
   * This field is autogenerated using the IDENTITY strategy.
   */
  @Id
  @GeneratedValue(strategy = GenerationType.IDENTITY)
  private int id;

  /**
   * Represents the name of a picture.
   * This field holds a textual description or title for the Picture entity.
   */
  private String name;

  /**
   * Represents the quantity of items available in stock for this Picture instance.
   */
  private int stock;

  /**
   * Represents the price of the picture.
   * The price is stored as a double value.
   */
  private double price;

  /**
   * A textual description of the picture.
   */
  private String description;

  /**
   * Creates an empty Picture instance.
   */
  public Picture() {
  }


  /**
   * Constructs a Picture instance with the specified attributes.
   *
   * @param name        the name of the picture
   * @param stock       the number of items in stock
   * @param price       the price of the picture
   * @param description the description of the picture
   */
  public Picture(String name, int stock, double price, String description) {
    this.name = name;
    this.stock = stock;
    this.price = price;
    this.description = description;
  }

  /**
   * Retrieves the unique identifier of this picture.
   *
   * @return the id of the picture
   */
  public int getId() {
    return id;
  }

  /**
   * Retrieves the name of the picture.
   *
   * @return the name of the picture as a String.
   */
  public String getName() {
    return name;
  }

  /**
   * Retrieves the stock value of this Picture instance.
   *
   * @return the stock value as an integer.
   */
  public int getStock() {
    return stock;
  }

  /**
   * Retrieves the price of the picture.
   *
   * @return the price of the picture as a double
   */
  public double getPrice() {
    return price;
  }

  /**
   * Retrieves the description of the picture.
   *
   * @return the description of the picture as a String.
   */
  public String getDescription() {
    return description;
  }

  /**
   * Sets the name of the picture.
   *
   * @param name the name to set, represented as a String
   */
  public void setName(String name) {
    this.name = name;
  }

  /**
   * Sets the stock quantity of the picture.
   *
   * @param stock the stock quantity to set, represented as an integer
   */
  public void setStock(int stock) {
    this.stock = stock;
  }

  /**
   * Sets the price of the picture.
   *
   * @param price the price to set, represented as a double
   */
  public void setPrice(double price) {
    this.price = price;
  }

  /**
   * Sets the description of the picture.
   *
   * @param description the description to set, represented as a String
   */
  public void setDescription(String description) {
    this.description = description;
  }
}
